import React,{useEffect, useState} from 'react';
import axios from "axios";
import './Header.css';


const Header = () => {

  const [data,setData]=useState({});

  const getWeatherDetails=(cityName) =>{
    if (!cityName) return
    const apiURL="https://api.openweathermap.org/data/2.5/weather?q="+cityName+"&appid=4a2becfa53e4c051c63a7ebc731855a0";
   axios.get(apiURL).then((res)=>{
   console.log("response",res.data);

   setData(res.data);

   }).catch((err)=>{
     console.log("err",err);
   })
  }

  useEffect (()=>{
    getWeatherDetails("delhi")
  },[]);

  return (
    <div className='header'>
      <div className='header-bg'>
      <h3 className='title'>Weather App</h3>
      <input type="text" className='form'  />
      <button className='btn'>Search</button>
      <div className="weather-result">

        <img className='logo' src="https://windy.app//storage/posts/November2021/02-partly-%20cloudy-weather-symbol-windyapp.jpg" alt="not found" />

        <div className="cityName">{data?.name}</div>
        <div className="temp">{(data?.main?.temp)-273.15} C</div>

      </div>
    
    </div>
    </div>
  );
}

export default Header;
